mapper.select:

SELECT  RATING_FLG
FROM    POST_RATINGS
WHERE   TOPICNO      =  #{topicNo}
AND     POSTNO       =  #{postNo}
AND     RATEDBY      =  #{user.id}

mapper.insert:

UPDATE  POSTS
SET		POST		=	#{postText},
		POST_IMG	=	#{postImg.encodedString}
WHERE 	TOPICNO		=	#{topicNo}
AND 	POSTNO 		=	#{postNo}


DELETE
FROM		POSTS
WHERE		TOPICNO		=	#{topicNo}
AND 		POSTNO 		=	#{postNo}
